const SubjectFormField = ({name, label, helpMessage}) => {
    const [selectedSubjects, setSelectedSubjects] = useState([])
    const taughtSubjects = ["Accountancy", "Biology", "Classics", "Creative Writing", "Economics", "French", "Geography", "German", "Mathematics", "Philosophy", "Photography"]

    const handleChange = (event) => {
        const {
          target: { value },
        } = event;
        setSelectedSubjects(
        //   typeof value === 'string' ? value.split(',') : value,
        value

        );
        console.log(value)
      };

    return (
        <>
        <FormControl sx={{ mb: 1, minWidth: 250, maxWidth: 350}}>
        <InputLabel id="subjects-label">{label}</InputLabel>
        <Select 
            labelId="subjects-label"
            id="subjects"
            multiple
            value={selectedSubjects}
            onChange={handleChange}
            input={<OutlinedInput id="select-multiple-chip" label="Subjects" />} 
            renderValue={(selected) => (
                <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
                    {selected.map((value) => (
                    <Chip key={value} label={value} />
                    ))}
                </Box>
                 )}
            >
            {taughtSubjects.map((subject) => (<MenuItem key={subject} value={subject}>{subject}</MenuItem>))}
        </Select>
        </FormControl>
        </>
    )
}
