import React, {useEffect, useState} from 'react';
import { Container, Typography,  Stack } from "@mui/material";
import { useMatch } from "@reach/router";
import Loader from "react-loader-spinner";

import StudentForm from './StudentForm';


const StudentView = () => {
    const {studentId} = useMatch('/student/:studentId');
    const [student, setStudent] = useState(null);

    useEffect(() => {
        const fetchStudentData = async () => {
          try {
            const res = await fetch(`http://localhost:4000/api/students/${studentId}`);
            if (!res.ok) {
              throw res;
            }
            const data = await res.json();
            const studentData = data.student[0];
            setStudent(studentData);
          } catch (err) {
            return { error: err };
          }
        };
        fetchStudentData();
      }, []);

    const handleSubmit = async (values) => {
     console.log("PATCH:", values)
     try {
      const res = await fetch(`http://localhost:4000/api/students/${studentId}`, { method: 'PATCH', body: values });
      if (!res.ok) {
        throw res;
      }
      console.log("res", res);
      if (res) {
         navigate(`/`)
      }
    } catch (err) {
      return { error: err };
    }
    }




    return (
        <>
       {student ?  <Container maxWidth="md">
            <Stack direction="column" spacing={2} sx={{position: "relative", top: "100px", alignItems:"center"}}>
                <Typography  variant="h5">Student view</Typography> 
            </Stack>
            <StudentForm initialValues={student} handleSubmit={handleSubmit} typeOfForm={"Existing Student"} studentId={studentId}/>

        </Container>
        : <Container maxWidth="md"> 
            <Stack sx={{position: "relative", top: "100px", alignItems:"center"}}>
            <Loader
                type="Puff"
                color="#00BFFF"
                height={100}
                width={100}
                alignItems="center"
            />
            </Stack>
            </Container> }

        </>
    )
}

export default StudentView
