              throw res;
            }
            const data = await res.json();
            console.log("1", data);
            const studentData = data.student[0];
            console.log("2", studentData);
            setStudent(studentData);
          } catch (err) {
            return { error: err };
          }
        };
        fetchStudentData();
      }, []);

