import React, {useEffect, useState} from 'react';
import { Container, Typography,  Stack, Box, TextField} from "@mui/material";
import { useMatch } from "@reach/router";
import Loader from "react-loader-spinner";

const StudentView = () => {
    const {studentId} = useMatch('/student/:studentId');
    const [student, setStudent] = useState({});

    useEffect(() => {
        fetch(`http://localhost:4000/api/students/${studentId}`)
        .then(res => res.json())
        .then((data) => {
        const studentData = data.student[0];
        setStudent(studentData);
        })
        .catch(error => console.error(error));
        },
        [])

    const {age, email, firstName, lastName, subjects} = student;
    console.log(age)

    if (!student){
        return
    }
    return (
        <>
        <Container maxWidth="md">
            <Stack direction="column" spacing={2} sx={{position: "relative", top: "100px", alignItems:"center"}}>
            {student ? <Typography  variant="h5">Student view</Typography> : <Loader
                type="Puff"
                color="#00BFFF"
                height={100}
                width={100}
                timeout={3000} //3 secs
                alignItems="center"
            />}
            </Stack>
            {student && <Box sx={{position: "relative", top: "150px", display:"flex", flexDirection:"column"}} spacing={2}>
                <div>
                <TextField sx={{m: 1}} required label="First name"  value={firstName}  />
                <TextField sx={{m: 1}} required label="Last name" value={lastName} />
                </div>
                <div>
                <TextField sx={{m: 1}} label="Age" value={age} touched/>
                </div>
                <div>
                {subjects && subjects.map((s, a) => <TextField sx={{m: 1}} value={s} label={`Subect ${a+1}`} />)}
                </div>
                <div>
                <TextField sx={{m: 1}} required value={email} label="Email" />
                </div>
            </Box>}
        </Container>

        </>
    )
}

export default StudentView
