import React from 'react';
import { Container, Typography,  Stack } from "@mui/material";
import { navigate } from "@reach/router";

import StudentForm from '../StudentForm/StudentForm';
import SubjectFormField from '../StudentForm/SubjectFormField';



const NewStudent = () => {

    const initialValues = {firstName: "", lastName: "", age: "", email: ""};

    const handleSubmit = async (values) => {
        console.log("POST:", values)
        try {
            const res = await fetch(`http://localhost:4000/api/students/`, { method: 'POST', body: values });
            if (!res.ok) {
              throw res;
            }
            console.log("res", res);
            if (res) {
               navigate(`/`)
            }
          } catch (err) {
            return { error: err };
          }
       }


    return (
        <Container maxWidth="md">
            <Stack direction="column" spacing={2} sx={{position: "relative", top: "100px", alignItems:"center"}}>
                <Typography  variant="h5">New student</Typography> 
            </Stack>
            <StudentForm initialValues={initialValues} handleSubmit={handleSubmit} typeOfForm={"New Student"}/>
            <div>
            <SubjectFormField />
            </div>

        </Container>

    )
}

export default NewStudent
