          const studentData = data.students;
          setStudents(studentData);
          })
          .catch(error => console.error(error));
           },
          [])


    return (
        <>
        <Container maxWidth="md">
            <Stack direction="column" spacing={2} sx={{position: "relative", top: "100px", alignItems:"center"}}>
                <Typography  variant="h5">Students</Typography>
                <Button variant="outlined" sx={{maxWidth:"150px", fontSize: '10px', height:"auto" }}>Create New</Button>
                <div>
                    {students ? students.map(student => <StudentCard key={`student-${student.id}`} student={student}/>) : <Loader
        type="Puff"
        color="#00BFFF"
        height={100}
        width={100}
      />}
                </div>
            </Stack>
        </Container>
        </>
    )
}

export default ListView
