import React, {useEffect, useState} from 'react';
import { Container, Typography,  Stack, Box, TextField} from "@mui/material";
import { useMatch } from "@reach/router";
import Loader from "react-loader-spinner";

const StudentView = () => {
    const {studentId} = useMatch('/student/:studentId');
    const [student, setStudent] = useState([]);

    useEffect(() => {
        fetch(`http://localhost:4000/api/students/${studentId}`)
        .then(res => res.json())
        .then((data) => {
        const studentData = data.student[0];
        setStudent(studentData);
        })
        .catch(error => console.error(error));
        },
        [])

    const {age, email, firstName, lastName, subjects} = student;

    return (
        <>
        {!student ? <Container maxWidth="md">
            <Stack direction="column" spacing={2} sx={{position: "relative", top: "100px", alignItems:"center"}}>
                <Typography  variant="h5">Student view</Typography>
            </Stack>
            <Box sx={{position: "relative", top: "150px", alignItems:"center"}}>
                <TextField>{firstName}</TextField>
                <TextField>{lastName}</TextField>
                <TextField>{age}</TextField>
                {subjects && subjects.map(s => <TextField>{s}</TextField>)}
                <TextField>{email}</TextField>
            </Box>
        </Container> :
        <Loader
        type="Puff"
        color="#00BFFF"
        height={100}
        width={100}
        timeout={3000} //3 secs
        sx={{position: "relative", top: "500px", alignItems:"center"}}
    />}
        </>
    )
}

export default StudentView
