const SubjectFormField = () => {
    const subjects = ["Accountancy", "Biology", "Classics", "Creative Writing", "Economics", "French", "Geography", "German", "Mathematics", "Philosophy", "Photography"]
    return (
        <>
        <Select input={<OutlinedInput label="Subjects" />} multiple
        renderValue={(selected) => (
            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
              {selected.map((value) => (
                <Chip key={value} label={value} />
              ))}
            </Box>
          )}>

            {subjects.map((subject) => {<MenuItem key={subject} value={subject}>{subject}</MenuItem>})}
        </Select>

        <Select
          labelId="demo-multiple-chip-label"
          id="demo-multiple-chip"
          multiple
          value={personName}
          onChange={handleChange}
          input={<OutlinedInput id="select-multiple-chip" label="Chip" />}
          renderValue={(selected) => (
            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
              {selected.map((value) => (
                <Chip key={value} label={value} />
              ))}
            </Box>
          )}
          MenuProps={MenuProps}
        >
          {names.map((name) => (
            <MenuItem
              key={name}
              value={name}
              style={getStyles(name, personName, theme)}
            >
              {name}
            </MenuItem>
          ))}
        </Select>
        </>
    )
}
export default SubjectFormField
