import React, { useContext } from "react";
import { Container, Typography, Stack } from "@mui/material";
import { navigate } from "@reach/router";

import StudentForm from "../StudentForm/StudentForm";
import { NotificationContext } from "../../context/NotificationProvider";

const NewStudent = () => {
  const { snack, setSnack } = useContext(NotificationContext);
  // console.log("snack", snack);

  const initialValues = {
    firstName: "",
    lastName: "",
    age: "",
    email: "",
    subjects: [],
  };

  const handleSubmit = async (values) => {
    try {
      const res = await fetch(`http://localhost:4000/api/students/`, {
        method: "POST",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
        body: JSON.stringify(values),
      });
      if (!res.ok) {
        // notificationContext.setSnack({
        //   message: "Sorry, there was an error! Please try again.",
        //   severity: "warning",
        //   open: true,
        // });
        throw res;
      } else {
        setSnack({
          message: "Student created!",
          severity: "success",
          open: true,
        });
        // console.log(notificationContext.snack);
        navigate(`/`);
      }
    } catch (err) {
      // notificationContext.setSnack({
      //   message: "Sorry, there was an error! Please try again.",
      //   severity: "warning",
      //   open: true,
      // });
      console.err(err);
      // console.log(err);
    }
  };

  return (
    <>
      <Container maxWidth="md">
        <Stack
          direction="column"
          spacing={2}
          sx={{ position: "relative", top: "100px", alignItems: "center" }}
        >
          <Typography variant="h5">New student</Typography>
        </Stack>
        <StudentForm
          initialValues={initialValues}
          handleSubmit={handleSubmit}
        />
      </Container>
    </>
  );
};

export default NewStudent;
