import { Formik, Form } from "formik";
import { Box, Button } from "@mui/material";
import * as Yup from "yup";

import React from 'react'
import FormField from "./FormField";

const StudentForm = ({student}) => {
    const {age, email, firstName, lastName} = student;

const validationSchema = Yup.object().shape({
    firstName: Yup.string().required(),
    lastName: Yup.string().required(),
    age: Yup.number().required().min(0).max(100),
    // subject: Yup.string().required(),
    email: Yup.string().email().required(),
    });


    return (
        <Formik initialValues={student}
        onSubmit={(values) => console.log("POST:", values)}
        validationSchema={validationSchema}
        > 
        {({isSubmitting}) => (
            <Form>
                <Box sx={{position: "relative", top: "150px", display:"flex", flexDirection:"column"}} spacing={2}>
                    <div>
                    <FormField name="firstName" label="First Name" helpMessage="Help Message"/>
                    <FormField name="lastName" label="Last Name" helpMessage="Help Message"/>
                    </div>
                    <div>
                    <FormField name="age" label="Age" helpMessage="Help Message"/>
                    </div>
                    {/* <div>
                    {subjects && subjects.map((s, i) => <FormField key={i} name={s} label={`Subject ${i+1}`} helpMessage={"Help Message"}/>) }
                    </div> */}
                    <div>
                    <FormField name={email} label={"Email"} helpMessage={"Help Message"}/>
                    </div>
                    <div>
                    <Button variant="contained" sx={{mr: 1, mb: 1}} type="submit" disabled={isSubmitting}>Submit</Button>
                    <Button variant="outlined" >Reset</Button>
                    </div>
                    <div>
                    <Button variant="outlined" color="error" sx={{mr: 1}}>Delete</Button>
                    </div>
                </Box>
            </Form>
        )} 
        </Formik>
    )
}

export default StudentForm
